mapper.select:
select

from hzero_platform.iam_user iu


        and iu.id = #{id,jdbcType = INTEGER}


        and iu.attribute1 = #{attribute1,jdbcType = VARCHAR}


        and iu.attribute10 = #{attribute10,jdbcType = VARCHAR}


        and iu.attribute11 = #{attribute11,jdbcType = VARCHAR}


        and iu.attribute12 = #{attribute12,jdbcType = VARCHAR}


        and iu.attribute13 = #{attribute13,jdbcType = VARCHAR}


        and iu.attribute14 = #{attribute14,jdbcType = VARCHAR}


        and iu.attribute15 = #{attribute15,jdbcType = VARCHAR}


        and iu.attribute2 = #{attribute2,jdbcType = VARCHAR}


        and iu.attribute3 = #{attribute3,jdbcType = VARCHAR}


        and iu.attribute4 = #{attribute4,jdbcType = VARCHAR}


        and iu.attribute5 = #{attribute5,jdbcType = VARCHAR}


        and iu.attribute6 = #{attribute6,jdbcType = VARCHAR}


        and iu.attribute7 = #{attribute7,jdbcType = VARCHAR}


        and iu.attribute8 = #{attribute8,jdbcType = VARCHAR}


        and iu.attribute9 = #{attribute9,jdbcType = VARCHAR}


        and iu.email = #{email,jdbcType = VARCHAR}


        and iu.HASH_PASSWORD = #{hashPassword,jdbcType = VARCHAR}


        and iu.image_url = #{imageUrl,jdbcType = VARCHAR}


        and iu.INTERNATIONAL_TEL_CODE = #{internationalTelCode,jdbcType = VARCHAR}


        and iu.is_admin = #{isAdmin,jdbcType = INTEGER}


        and iu.is_enabled = #{isEnabled,jdbcType = INTEGER}


        and iu.is_ldap = #{isLdap,jdbcType = INTEGER}


        and iu.is_locked = #{isLocked,jdbcType = INTEGER}


        and iu.language = #{language,jdbcType = VARCHAR}


        and iu.last_login_at = #{lastLoginAt,jdbcType = TIMESTAMP}


        and iu.last_password_updated_at = #{lastPasswordUpdatedAt,jdbcType = TIMESTAMP}


        and iu.locked_until_at = #{lockedUntilAt,jdbcType = TIMESTAMP}


        and iu.login_name = #{loginName,jdbcType = VARCHAR}


        and iu.organization_id = #{organizationId,jdbcType = INTEGER}


        and iu.password_attempt = #{passwordAttempt,jdbcType = INTEGER}


        and iu.phone = #{phone,jdbcType = VARCHAR}


        and iu.profile_photo = #{profilePhoto,jdbcType = OTHER}


        and iu.quick_index = #{quickIndex,jdbcType = VARCHAR}


        and iu.real_name = #{realName,jdbcType = VARCHAR}


        and iu.time_zone = #{timeZone,jdbcType = VARCHAR}


        and iu.user_type = #{userType,jdbcType = VARCHAR}
